import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  isCartVis: false,
};

const uiSlice = createSlice({
  name: 'ui',
  initialState,
  reducers: {
    toggleSidebar(state) {
      state.isCartVis = !state.isCartVis;
    },
  },
});


export const { toggleSidebar } = uiSlice.actions;

export default uiSlice.reducer;
