import classes from './CartItem.module.css';
import { useDispatch } from 'react-redux';
import { addItemToCart, removeItemFromCart } from '../../store/features/cartSlice';

const CartItem = (props) => {
  const { id, title, quantity, total, price, description } = props.item;
  const dispatch = useDispatch();

  const newItem = {title, price, description, id: id, quantity: 1, totalPrice: price};

  return (
    <li className={classes.item}>
      <header>
        <h3>{title}</h3>
        <div className={classes.price}>
          ${total.toFixed(2)}{' '}
          <span className={classes.itemprice}>(${price.toFixed(2)}/item)</span>
        </div>
      </header>
      <div className={classes.details}>
        <div className={classes.quantity}>
          x <span>{quantity}</span>
        </div>
        <div className={classes.actions}>
          <button onClick={() => dispatchremoveItemFromCart(id)}>-</button>
          <button onClick={() => addItemToCart(newItem)}>+</button>
        </div>
      </div>
    </li>
  );
};

export default CartItem;
